for(var width_mod=document.getElementsByClassName("js-modifier-width"),height_mod=document.getElementsByClassName("js-modifier-height"),i=0;i<width_mod.length;i++)width_mod[i].checked=!1,width_mod[i].initialWidth=width_mod[i].parentElement.offsetWidth,width_mod[i].parentElement.style.width=width_mod[i].initialWidth+"px";for(i=0;i<height_mod.length;i++)height_mod[i].checked=!1,height_mod[i].initialHeight=height_mod[i].parentElement.offsetHeight,height_mod[i].parentElement.style.height=height_mod[i].initialHeight+"px";for(i=0;i<width_mod.length;i++)width_mod[i].addEventListener("change",function(){this.checked?this.parentElement.style.width="150px":this.parentElement.style.width=this.initialWidth+"px"});for(i=0;i<height_mod.length;i++)height_mod[i].addEventListener("change",function(){this.checked?this.parentElement.style.height="150px":this.parentElement.style.height=this.initialHeight+"px"});